// Data Normalization and Preparation
// This replaces the 'Simulation' node. It ensures required fields exist without overwriting real data.

const leads = [];

for (const item of $input.all()) {
  const lead = item.json;

  // Normalize data (Use existing data or set safe defaults)
  const prepared = {
    ...lead,
    // Default to 'Unknown' if missing, required for fit score
    company_size: lead.company_size || 'Unknown',
    industry: lead.industry || 'Unspecified',

    // Convert 'true'/'false' strings to booleans if necessary
    email_verified: lead.email_verified === true || lead.email_verified === 'true',

    // Ensure website format
    website: lead.website || (lead.company ? `https://${lead.company.toLowerCase().replace(/\s+/g, '')}.com` : null),

    // Add processing timestamp
    processing_date: new Date().toISOString()
  };

  leads.push({ json: prepared });
}

return leads;

