// Parse Groq response and calculate composite score
const lead = $('Loop Over Leads').item.json;

let groqResponse;
try {
  groqResponse = JSON.parse($json.choices[0].message.content);
} catch (e) {
  // Fallback if AI fails to return valid JSON
  groqResponse = { buying_intent_score: 5, pain_points: [] };
}

// Composite scoring algorithm
const scores = {
  ai_intent: (groqResponse.buying_intent_score || 5) * 0.4, // 40%
  engagement: (parseFloat(lead.engagement_level) || 5) * 0.3, // 30% (Default to 5 if missing)
  fit_score: calculateFitScore(lead) * 0.2, // 20%
  recency: calculateRecencyScore(lead) * 0.1 // 10%
};

const composite_score = Object.values(scores).reduce((a, b) => a + b, 0);

// Helper functions
function calculateFitScore(lead) {
  let score = 5; // baseline

  if (['SaaS', 'Finance', 'Technology'].includes(lead.industry)) score += 2;
  if (lead.company_size === '50-200' || lead.company_size === '200-500') score += 2;
  if (lead.linkedin_quality && lead.linkedin_quality > 7) score += 1;

  return Math.min(score, 10);
}

function calculateRecencyScore(lead) {
  // Real implementation: Check last_contact_date
  if (!lead.last_contact_date) return 5; // Neutral if unknown

  const daysSince = (new Date() - new Date(lead.last_contact_date)) / (1000 * 60 * 60 * 24);
  if (daysSince < 7) return 10;
  if (daysSince < 30) return 7;
  return 3;
}

return [{
  json: {
    ...lead,
    ai_analysis: groqResponse,
    composite_score: parseFloat(composite_score.toFixed(2)),
    score_breakdown: scores,
    analysis_timestamp: new Date().toISOString()
  }
}];

