// Generate summary report
const allLeads = $input.all().map(item => item.json);

const summary = {
  total_leads: allLeads.length,
  high_intent: allLeads.filter(l => l.composite_score >= 8).length,
  medium_intent: allLeads.filter(l => l.composite_score >= 5 && l.composite_score < 8).length,
  low_intent: allLeads.filter(l => l.composite_score < 5).length,
  average_score: allLeads.length > 0 ? (allLeads.reduce((sum, l) => sum + l.composite_score, 0) / allLeads.length).toFixed(2) : 0,
  top_industries: getTopItems(allLeads, 'industry'),
  common_pain_points: getTopPainPoints(allLeads),
  generated_at: new Date().toISOString()
};

function getTopItems(arr, key) {
  const counts = {};
  arr.forEach(item => {
    counts[item[key]] = (counts[item[key]] || 0) + 1;
  });
  return Object.entries(counts)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 3)
    .map(([name, count]) => `${name} (${count})`);
}

function getTopPainPoints(leads) {
  const allPainPoints = leads
    .flatMap(l => l.ai_analysis?.pain_points || [])
    .filter(p => p);

  const counts = {};
  allPainPoints.forEach(p => {
    counts[p] = (counts[p] || 0) + 1;
  });

  return Object.entries(counts)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 5)
    .map(([pain, count]) => `${pain} (${count} leads)`);
}

return [{ json: { summary, detailed_leads: allLeads } }];

